mapper.select:
SELECT t1.num num, t1.writer writer, t1.content content, t1.postNum postNum, t1.parentNum parentNum, t1.depth depth, t1.regdate regdate, t1.likeCnt likeCnt, t1.dislikeCnt dislikeCnt, t1.rnum rnum, t2.pic pic FROM (
	SELECT result1.*, ROWNUM rnum FROM (
		SELECT * FROM c_board_free_comment
		WHERE postNum = #{postNum}
		START WITH parentNum = 0
		CONNECT BY PRIOR num = parentNum
		ORDER SIBLINGS BY num
	) result1
) t1,
(SELECT pic, nickname FROM c_user) t2 
WHERE t1.writer = t2.nickname AND t1.rnum BETWEEN #{startRowNum} AND #{endRowNum}

SELECT count(*) 
FROM c_board_free_comment
WHERE postNum= #{postNum}

mapper.update:
UPDATE c_user
SET point = point + 1
WHERE id = #{id}
